String blocStub(String name) {
  return '''
${_stamp()}  

${_buildImports()}
${_buildClassHeader(name)}
${_buildClassBody(name)}
}
''';
}

String _stamp() {
  return '''
// **************************************************************************
// GENERATED BY ULTRON @ ${DateTime.now()}
// **************************************************************************''';
}

String _buildImports() {
  final StringBuffer importsBuffer = StringBuffer();
  importsBuffer.writeln('import \'package:renderer/bloc/renderer_bloc.dart\';');
  importsBuffer.writeln('import \'exports.dart\';');
  return importsBuffer.toString();
}

String _buildClassHeader(String name) {
  return 'class ${name}Bloc extends RendererBLoC<${name}Event, ${name}State> {';
}

String _buildClassBody(String name) {
  return '''
  @override
  void dispatch(${name}Event event) async {}

  @override
  void dispose() {
    closeSubject();
  }''';
}
