String blocStateStub(String name) {
  return '''
${_stamp()}

${_buildImports()}
${_buildClassHeader(name)}
  ''';
}

String _stamp() {
  return '''
// **************************************************************************
// GENERATED BY ULTRON @ ${DateTime.now()}
// **************************************************************************''';
}

String _buildImports() {
  final StringBuffer importsBuffer = StringBuffer();
  importsBuffer.writeln('import \'/podo/exports.dart\';');
  importsBuffer
      .writeln('import \'package:renderer/bloc/renderer_state.dart\';');
  return importsBuffer.toString();
}

String _buildClassHeader(String name) {
  return 'abstract class ${name}State extends RendererState {}';
}
